import { useContext, useState } from "react";
import styles from "./CartBtn.module.scss";
import Icon from "../../../assets/Images/delivery.png";
import FoodContext from "../../../store/food-context";

const CartBtn = (props) => {
  const [isItemAdded, setIsItemAdded] = useState(false);

  const ctx = useContext(FoodContext);
  const { items } = ctx;

  let totalItems = 0;
  for (let i = 0; i < items.length; i++) {
    totalItems += items[i].amount;
  }

  // // Check the localStorage for add pump class to the cart
  // if (localStorage.getItem("addItem")) {
  //   setIsItemAdded(true);
  //   console.log(`Added`);
  //   localStorage.removeItem("addItem");
  // } else {
  //   setIsItemAdded(false);
  // }

  return (
    <button
      className={`${styles.cart} ${isItemAdded ? styles.bump : ""}`}
      onClick={props.onClickCart}
    >
      <img src={Icon} alt="delivery-icon" />
      <h3>Cart</h3>
      <span>{totalItems}</span>
    </button>
  );
};

export default CartBtn;
