import { Fragment,useEffect,useState } from "react";
import styles from "./ProductItem.module.scss";
const URL ="https://food-delivery-b0655-default-rtdb.firebaseio.com/food.json";

const ProductItem = () => {
  // States to mange data loading and errors
  const [error, setError] = useState('');

  // 
  useEffect(() => {
    const fetchOrSendRequest = async () => {
      const response = await fetch(URL);
      if (!response.ok) throw new Error("Fetch food failed!");
  
        const data = await response.json();
        console.log(data);
    };
    
    try {
      fetchOrSendRequest();
    } catch (error) {
      setError(error || 'Something went wrong!')
    }
    
  }, [])
  

  return <Fragment>FoodItems</Fragment>;
};

export default ProductItem;
